/**
* @access protected
*/
export default {
    commands: {
        bold: {
            tags: ['B', 'STRONG'],
            togglable: true
        },
        italic: {
            tags: ['I'],
            togglable: true
        },
        formatBlock: {
            tags: [
                'H1', 'H2', 'H3', 'H4', 'H5', 'H6', 'P',
                'BLOCKQUOTE'
            ],
            unwrapIfIn: [
                'bold',
                'formatBlock',
                'insertOrderedList',
                'insertUnorderedList'
            ]
        },
        insertOrderedList: {
            tags: ['OL', 'LI'],
            unwrapIfIn: [
                'formatBlock'
            ],
            togglable: true
        },
        insertUnorderedList: {
            tags: ['UL', 'LI'],
            unwrapIfIn: [
                'formatBlock'
            ],
            togglable: true
        },
        createLink: {
            tags: ['A']
        }
    },
    defaultBlock: 'P',
    paste: {
        invalidTags: ['SCRIPT', 'LINK', 'IMG']
    },
    global: {},
    toolbar: {},
    contentEditable: {}
};

/**
* @access protected
*/
const formatters = {
    base: {
        blockElementNames: [
            'blockquote', 'h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'ol', 'ul', 'li', 'p', 'pre',
            'address', 'article', 'aside', 'canvas', 'dd', 'div', 'dl', 'dt',
            'fieldset', 'figcaption', 'figure', 'footer', 'form', 'header', 'hgroup',
            'hr', 'main', 'nav', 'noscript', 'output', 'section', 'table', 'tfoot',
            'video'
        ]
    },

    text: {
        nodeLevel: Node.TEXT_NODE,
        changeLevel: {
            DOM: true,
            styles: false
        }
    },

    paragraph: {
        nodeLevel: Node.ELEMENT_NODE,
        changeLevel: {
            DOM: false,
            styles: true
        }
    },

    list: {
        nodeLevel: Node.ELEMENT_NODE,
        changeLevel: {
            DOM: true,
            styles: false
        },
        rootTags: ['ol', 'ul'],
        methods: {
            orderedlist: {
                rootTag: 'ol',
                tags: ['ol', 'li']
            },
            unorderedlist: {
                rootTag: 'ul',
                tags: ['ul', 'li']
            }
        }
    },

    block: {
        defaultElement: 'p',
        baseElement: 'div',
        nodeLevel: Node.ELEMENT_NODE,
        changeLevel: {
            DOM: true,
            styles: false
        }
    }
};

export default formatters;

// jshint strict: false

import linkIcon from '../../templates/icons/link.html';
import orderedlistIcon from '../../templates/icons/orderedlist.html';
import unorderedlistIcon from '../../templates/icons/unorderedlist.html';
import quoteIcon from '../../templates/icons/quote.html';

/**
* @access protected
*/
const Toolbar = {
    buttons: ['bold', 'italic', 'h1', 'h2', 'orderedlist', 'unorderedlist', 'quote', 'link'],
    preventNewlineDefault: ['ul', 'ol'],
    blockTags: ['P'],
    validTags: ['P'],
    listTags: [],
    getValidTags () {
        let { validTags } = Toolbar;

        if ( validTags.length === 1 ) {
            Toolbar.parseForTagLists();
        }

        return Toolbar.validTags;
    },
    getBlockTags () {
        let { blockTags } = Toolbar;

        if ( blockTags.length === 1 ) {
            Toolbar.parseForTagLists();
        }

        return Toolbar.blockTags;

    },
    getListTags () {
        let { listTags } = Toolbar;

        if ( listTags.length === 0 ) {
            Toolbar.parseForTagLists();
        }

        return Toolbar.listTags;
    },
    parseForTagLists () {
        let {
            validTags,
            blockTags,
            listTags
        } = Toolbar;

        Toolbar.buttons.forEach((buttonKey) => {
            let buttonConfig = Toolbar.buttonConfigs[buttonKey];
            let configValidTags = buttonConfig.opts.validTags;

            validTags = validTags.concat(configValidTags);

            switch (buttonConfig.formatter) {
            case 'block':
                blockTags = blockTags.concat(configValidTags);
                break;
            case 'list':
                listTags = listTags.concat(configValidTags);
                break;
            }
        });

        Toolbar.validTags = validTags;
        Toolbar.blockTags = blockTags;
        Toolbar.listTags  = listTags;
    },
    buttonConfigs: {
        // Text styles
        bold: {
            formatter: 'text',
            opts: {
                style: 'bold',
                rootEl: 'b',
                validTags: ['B', 'STRONG']
            },
            content: '<b>B</b>',
            disabledIn: ['H1', 'H2', 'BLOCKQUOTE'],
            activeIn: ['B']
        },

        italic: {
            formatter: 'text',
            opts: {
                style: 'italic',
                rootEl: 'i',
                validTags: ['I']
            },
            content: '<i>I</i>',
            activeIn: ['I']
        },

        underline: {
            formatter: 'text:underline',
            content: '<u>U</u>'
        },

        strikethrough: {
            formatter: 'text:strikethrough',
            content: '<s>A</s>'
        },

        superscript: {
            formatter: 'text:superscript',
            content: 'x<sup>1</sup>'
        },

        subscript: {
            formatter: 'text:subscript',
            content: 'x<sub>1</sub>'
        },

        // Paragraph styles
        justifyCenter: {
            formatter: 'paragraph:justifyCenter'
        },

        justifyFull: {
            formatter: 'paragraph:justifyFull'
        },

        justifyLeft: {
            formatter: 'paragraph:justifyLeft'
        },

        justifyRight: {
            formatter: 'paragraph:justifyRight'
        },

        indent: {
            formatter: 'paragraph:indent'
        },

        outdent: {
            formatter: 'paragraph:outdent'
        },

        // Lists
        orderedlist: {
            formatter: 'list',
            content: orderedlistIcon({}, {}, true),
            opts: {
                style: 'ordered',
                validTags: ['OL', 'LI']
            },
            activeIn: ['OL']
        },

        unorderedlist: {
            formatter: 'list',
            content: unorderedlistIcon({}, {}, true),
            opts: {
                style: 'unordered',
                validTags: ['UL', 'LI']
            },
            activeIn: ['UL']
        },

        // Block level elements
        quote: {
            formatter: 'block',
            content: quoteIcon({}, {}, true),
            opts: {
                style: 'BLOCKQUOTE',
                validTags: ['BLOCKQUOTE']
            },
            activeIn: ['BLOCKQUOTE'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        pre: {
            formatter: 'block',
            opts: {
                style: 'PRE'
            },
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            content: 'PRE'
        },

        h1: {
            formatter: 'block',
            opts: {
                style: 'H1',
                validTags: ['H1']
            },
            content: 'H1',
            activeIn: ['H1'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        h2: {
            formatter: 'block',
            opts: {
                style: 'H2',
                validTags: ['H2']
            },
            content: 'H2',
            activeIn: ['H2'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        h3: {
            formatter: 'block',
            opts: {
                style: 'H3',
                validTags: ['H3']
            },
            content: 'H3',
            activeIn: ['H3'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        h4: {
            formatter: 'block',
            opts: {
                style: 'H4',
                validTags: ['H4']
            },
            content: 'H4',
            activeIn: ['H4'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        h5: {
            formatter: 'block',
            opts: {
                style: 'H5',
                validTags: ['H5']
            },
            content: 'H5',
            activeIn: ['H5'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        h6: {
            formatter: 'block',
            opts: {
                style: 'H6',
                validTags: ['H6']
            },
            content: 'H6',
            activeIn: ['H6'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:in:or:contains', ['OL', 'UL']);
                disabled = disabled || mediator.get('selection:spans:multiple:blocks');
                return disabled;
            },
            toggles: true
        },

        // Link
        link: {
            formatter: 'link',
            opts: {
                validTags: ['A']
            },
            content: linkIcon({}, {}, true),
            activeIn: ['A'],
            disabledIn (mediator) {
                let disabled = mediator.get('selection:spans:multiple:blocks');
                return disabled;
            }
        }
    }
};

export default Toolbar;

// jshint strict: false

import Container from '../core/Container';

import UIContainer from '../containers/UIContainer';
import FormatterContainer from '../containers/FormatterContainer';
import CanvasContainer from '../containers/CanvasContainer';

import ContentEditable from '../modules/ContentEditable';
import Selection from '../modules/Selection';

let uiContainer, formatterContainer, canvasContainer;


/**
 * ### AppContainer
 * The top most container for the Typester app stack. This
 * container sets up the {@link FormatterContainer}, {@link UIContainer},
 * and {@link CanvasContainer} containers which in return setup various
 * modules that make up the app stack.
 *
 * @access protected
 * @param  {object} opts={} instance options
 * @param  {element} opts.dom.el The dom element to be the canvas for Typester
 * @return {appContainer} AppContainer instance
 *
 * @example
 * new AppContainer({
 *   dom: {
 *     el: domElement
 *   }
 * });
 */
const AppContainer = Container({
    name: 'AppContainer',
    modules: [
        {
            class: ContentEditable
        },
        {
            class: Selection
        }
    ],
    handlers: {
        events: {
            'contenteditable:focus': 'handleFocus',
            'contenteditable:blur': 'handleBlur'
        }
    },
    methods: {
/**
 * AppContainer:setup() - setup the child containers used to group other app components
 */
        setup () {
            const { mediator } = this;
            formatterContainer = formatterContainer || new FormatterContainer({ mediator });
            uiContainer = uiContainer || new UIContainer({ mediator });
            canvasContainer = canvasContainer || new CanvasContainer({ mediator });
        },
        init () {
        },

        handleFocus () {
            const { mediator } = this;
            uiContainer.setMediatorParent(mediator);
            formatterContainer.setMediatorParent(mediator);
            canvasContainer.setMediatorParent(mediator);
        },

        handleBlur () {
            // Should the container require to do anything in particular here
        }
    }
});

export default AppContainer;

// jshint strict: false

import Container from '../core/Container';
import Selection from '../modules/Selection';
import Canvas from '../modules/Canvas';

/**
* @access protected
*/
const CanvasContainer = Container({
    name: 'CanvasContainer',
    modules: [
        { class: Selection },
        { class: Canvas }
    ],
    mediatorOpts: {
        conceal: [
            /selection:.*?/
        ]
    },
    handlers: {
        events: {
            'canvas:created' : 'handleCanvasCreated'
        }
    },
    methods: {
        init () {
        },

        handleCanvasCreated () {
            const { mediator } = this;
            const canvasWin = mediator.get('canvas:window');
            const canvasDoc = mediator.get('canvas:document');
            const canvasBody = mediator.get('canvas:body');

            mediator.exec('selection:set:contextWindow', canvasWin);
            mediator.exec('selection:set:contextDocument', canvasDoc);
            mediator.exec('selection:set:el', canvasBody);
        }
    }
});

export default CanvasContainer;

