// jshint strict: false

import Mediator from './Mediator';
import Context from './Context';
import func from '../utils/func';

/**
* @access protected
*/
const Container = function Container(containerObj) {
    const {
        name: containerName,
        handlers: containerHandlers,
        methods: containerMethods,
        modules: containerModules,
        containers: containerChildContainers,
        mediatorOpts
    } = containerObj;

    if (!containerName) {
        throw new Error('No name given for container');
    }

    const containerUtils = {
        createContext (...contexts) {
            return new Context(...contexts);
        },

        bindMethods (methods, context) {
            methods = methods || {};
            return func.bindObj(methods, context);
        },

        initModules (modules=[], opts={}) {
            modules.forEach((module) => {
                const moduleOpts = Object.assign({}, opts, (module.opts || {}));
                module.instance = new module.class(moduleOpts);
            });
        },

        initChildContainers (childContainers=[], opts={}) {
            childContainers.forEach((containerObj) => {
                const containerOpts = Object.assign({}, opts, (containerObj.opts || {}));
                containerObj.instance = new containerObj.class(containerOpts);
            });
        },

        registerHandlers (mediator, handlers, context) {
            Object.keys(handlers).forEach((handlerKey) => {
                const handlerMap = handlers[handlerKey];
                let handlerMethods = containerUtils.getHandlerMethods(handlerMap, context);

                switch (handlerKey) {
                case 'requests':
                    mediator.registerRequestHandlers(handlerMethods);
                    break;
                case 'commands':
                    mediator.registerCommandHandlers(handlerMethods);
                    break;
                case 'events':
                    mediator.registerEventHandlers(handlerMethods);
                    break;
                }
            });
        },

        getHandlerMethods (handlerMap, context) {
            let routedHandlers = {};

            Object.keys(handlerMap).forEach((commandStr) => {
                const methodKey = handlerMap[commandStr];
                const handlerMethod = context[methodKey];
                routedHandlers[commandStr] = handlerMethod;
            });

            return routedHandlers;
        }
    };

    const containerProto = {
        containerConstructor: function (opts={}) {
            const context = containerUtils.createContext();
            const boundMethods = containerUtils.bindMethods(containerMethods, context);
            context.extendWith(boundMethods);
            const mediator = new Mediator(Object.assign({ parent: opts.mediator }, mediatorOpts));
            context.extendWith({ mediator });

            if (containerHandlers) {
                containerUtils.registerHandlers(mediator, containerHandlers, context);
            }

            if (boundMethods.setup) {
                boundMethods.setup();
            }

            containerUtils.initModules(containerModules, {
                dom: opts.dom,
                mediator
            });

            containerUtils.initChildContainers(containerChildContainers, {
                dom: opts.dom,
                mediator
            });

            if (boundMethods.init) {
                boundMethods.init();
            }

            return {
                setMediatorParent (parentMediator) {
                    mediator.setParent(parentMediator);
                }
            };
        }
    };

    return containerProto.containerConstructor;
};

export default Container;

// jshint strict: false

/**
* @access protected
*/
const Context = function (...contexts) {
    this.mixin(...contexts);
};

Object.assign(Context.prototype, {
    mixin (...contexts) {
        contexts.forEach((context) => {
            Object.assign(this, context);
        });
    },

    extendWith (mixinContext, opts={}) {
        if (opts.keys) {
            opts.keys.forEach((key) => {
                this[key] = mixinContext[key];
            });
        } else {
            this.mixin(mixinContext);
        }
    }
});

export default Context;

// jshint strict: false
import guid from '../utils/guid';

/**
* @access protected
*/
const Mediator = function (opts={}) {
    const internal = {
        parent: opts.parent,
        children: [],
        id: guid(),
        conceal: opts.conceal || []
    };

    const requests = {
        handlers: {},

        new (requestKey, requestHandler) {
            if (requests.handlers[requestKey]) {
                throw new Error(`Only one requestHandler per requestKey: ${requestKey}`);
            }

            requests.handlers[requestKey] = requestHandler;
        },

        getHandler (requestKey) {
            return requests.handlers[requestKey];
        },

        canHandle (requestKey) {
            return !!requests.getHandler(requestKey);
        },

        request (requestKey, options) {
            const requestHandler = requests.getHandler(requestKey);

            if (requestHandler) {
                return requestHandler(options);
            }
        }
    };

    const commands = {
        handlers: {},

        new (commandKey, commandHandler) {
            if (commands.handlers[commandKey]) {
                throw new Error(`Only one commandHandler per commandKey: ${commandKey}`);
            }

            commands.handlers[commandKey] = commandHandler;
        },

        getHandler (commandKey) {
            return commands.handlers[commandKey];
        },

        canHandle (commandKey) {
            return !!commands.getHandler(commandKey);
        },

        exec (commandKey, options) {
            const commandHandler = commands.getHandler(commandKey);
            if (commandHandler) {
                commandHandler(options);
            }
        }
    };

    const events = {
        handlers: {},

        new (eventKey, eventHandler) {
            events.handlers[eventKey] = events.handlers[eventKey] || [];
            events.handlers[eventKey].push(eventHandler);
        },

        getHandlers (eventKey) {
            return events.handlers[eventKey] || [];
        },

        canHandle (eventKey) {
            return !!events.getHandlers(eventKey);
        },

        emit (eventKey, options) {
            const eventHandlers = events.getHandlers(eventKey);
            if (eventHandlers.length) {
                eventHandlers.forEach((eventHandler) => eventHandler(options));
            }
        }
    };

    const registers = {
        registerHandler (type, typeKey, typeHandler) {
            switch (type) {
            case 'request':
                requests.new(typeKey, typeHandler);
                break;
            case 'command':
                commands.new(typeKey, typeHandler);
                break;
            case 'event':
                events.new(typeKey, typeHandler);
                break;
            }
        },

        registerRequestHandlers (requestHandlers={}) {
            Object.keys(requestHandlers).forEach((requestKey) => {
                let requestHandler = requestHandlers[requestKey];
                registers.registerHandler('request', requestKey, requestHandler);
            });
        },

        registerCommandHandlers (commandHandlers={}) {
            Object.keys(commandHandlers).forEach((commandKey) => {
                let commandHandler = commandHandlers[commandKey];
                registers.registerHandler('command', commandKey, commandHandler);
            });
        },

        registerEventHandlers (eventHandlers={}) {
            Object.keys(eventHandlers).forEach((eventKey) => {
                let eventHandler = eventHandlers[eventKey];
                registers.registerHandler('event', eventKey, eventHandler);
            });
        }
    };

    const fn = {
        init () {
            if (internal.parent) {
                internal.parent.registerChild(api);
            }
        },

        shouldConceal (msgKey) {
            let shouldConceal = false;

            internal.conceal.forEach((concealPattern) => {
                shouldConceal = shouldConceal || concealPattern.test(msgKey);
            });

            return shouldConceal;
        },

        delegate (type, msgKey, args={}, opts={}) {
            const { _state } = opts;

            if (!_state.hasAttempted(internal.id)) {
                fn.handle(type, msgKey, args, opts);
            }

            if (!_state.hasBeenHandled) {
                internal.children.forEach((childMediator) => {
                    if (
                        !_state.hasBeenHandled &&
                        !_state.hasAttempted(childMediator.getId())
                    ) {
                        childMediator.handle(type, msgKey, args, opts);
                    }
                });
            }

            if (!_state.hasBeenHandled) {
                if (
                    internal.parent &&
                    !_state.hasAttempted(internal.parent.getId()) &&
                    !fn.shouldConceal(msgKey)
                ) {
                    internal.parent.delegate(type, msgKey, args, opts);
                }
            }

            return _state.response;
        },

        canHandle (type, msgKey) {
            switch (type) {
            case 'request':
                return requests.canHandle(msgKey);
            case 'command':
                return commands.canHandle(msgKey);
            case 'event':
                return events.canHandle(msgKey);
            }
        },

        handle (type, msgKey, args={}, opts={}) {
            opts._state = opts._state || fn.newStateObj();

            const canHandle = fn.canHandle(type, msgKey);
            const { _state } = opts;

            if (_state.hasAttempted(internal.id)) {
                return _state.response;
            }

            opts._state.logAttempt(internal.id);
            if (canHandle) {
                switch (type) {
                case 'request':
                    _state.response = requests.request(msgKey, args);
                    _state.hasBeenHandled = true;
                    break;
                case 'command':
                    commands.exec(msgKey, args);
                    _state.hasBeenHandled = true;
                    break;
                case 'event':
                    events.emit(msgKey, args);
                    fn.delegate(type, msgKey, args, opts);
                    break;
                }

                return _state.response;
            } else {
                return fn.delegate(type, msgKey, args, opts);
            }
        },

        newStateObj () {
            const stateObj = {
                attempts: [],
                hasBeenHandled: false,
                response: null,
                logAttempt (mediatorId) {
                    this.attempts.push(mediatorId);
                },
                hasAttempted (mediatorId) {
                    return this.attempts.indexOf(mediatorId) > -1;
                }
            };

            return stateObj;
        },

        request (requestKey, opts={}) {
            return fn.handle('request', requestKey, opts);
        },

        exec (commandKey, opts={}) {
            fn.handle('command', commandKey, opts);
        },

        emit (eventKey, opts={}) {
            fn.handle('event', eventKey, opts);
        },

        hasAttemptedToHandle (opts) {
            return opts.attemptedDelegates.indexOf(internal.id) > -1;
        },

        registerChild (childApi) {
            childApi.childIndex = internal.children.length;
            internal.children.push(childApi);
        },

        deregisterChild (childApi) {
            internal.children.slice(childApi.childIndex, 1);
        },

        setParent (parentApi) {
            if (internal.parent) {
                internal.parent.deregisterChild(api);
            }

            internal.parent = parentApi;
            internal.parent.registerChild(api);
        },

        getId () {
            return internal.id;
        }
    };

    const api = {
        getId: fn.getId,
        registerRequestHandlers: registers.registerRequestHandlers,
        registerCommandHandlers: registers.registerCommandHandlers,
        registerEventHandlers: registers.registerEventHandlers,
        registerHandler: registers.registerHandler,
        request: fn.request,
        exec: fn.exec,
        emit: fn.emit,
        get: fn.request,
        canHandle: fn.canHandle,
        handle: fn.handle,
        registerChild: fn.registerChild,
        hasAttemptedToHandle: fn.hasAttemptedToHandle,
        deregisterChild: fn.deregisterChild,
        setParent: fn.setParent,
        delegate: fn.delegate,
        getHandlerKeys: function () {
            let handlers = [];

            handlers.concat(Object.keys(requests.handlers));
            handlers.concat(Object.keys(commands.handlers));
            handlers.concat(Object.keys(events.handlers));

            return handlers;
        }
    };

    fn.init();

    return api;
};

export default Mediator;

// jshint strict: false

import Context from './Context';
import func from '../utils/func';
import DOM from '../utils/DOM';

/**
* @access protected
*/
const Module = function (moduleObj) {
    const {
        name: moduleName,
        props: moduleProps,
        handlers: moduleHandlers,
        dom: moduleDom,
        methods: moduleMethods,
        requiredProps: moduleRequiredProps
    } = moduleObj;

    if (!moduleName) {
        throw new Error('No name given for module', moduleObj);
    }

    const moduleUtils = {
        createContext (...contexts) {
            return new Context(...contexts);
        },

        bindMethods (methods, context) {
            methods = methods || {};
            return func.bindObj(methods, context);
        },

        wrapRenderMethod (renderMethod, opts) {
            const wrappedRenderMethod = function (...args) {
                let { context } = opts;
                let mergedDom;

                mergedDom = moduleUtils.mergeDom(moduleDom, opts.dom);
                mergedDom.el = renderMethod(...args);

                moduleUtils.getDom(mergedDom);
                context.extendWith({ dom: mergedDom });

                if (moduleHandlers.domEvents) {
                    moduleUtils.registerDomHandlers(moduleHandlers.domEvents, context);
                }
            };
            return wrappedRenderMethod;
        },

        registerHandlers (mediator, handlers, context) {
            Object.keys(handlers).forEach((handlerKey) => {
                const handlerMap = handlers[handlerKey];
                let handlerMethods = moduleUtils.getHandlerMethods(handlerMap, context);
                switch (handlerKey) {
                case 'requests':
                    mediator.registerRequestHandlers(handlerMethods);
                    break;
                case 'commands':
                    mediator.registerCommandHandlers(handlerMethods);
                    break;
                case 'events':
                    mediator.registerEventHandlers(handlerMethods);
                    break;
                }
            });
        },

        registerDomHandlers (domHandlersMap, context) {
            let handlerMethods = moduleUtils.getHandlerMethods(domHandlersMap, context);
            moduleUtils.bindDomEvents(handlerMethods, context);
        },

        getHandlerMethods (handlerMap, context) {
            let routedHandlers = {};

            Object.keys(handlerMap).forEach((commandStr) => {
                const methodKey = handlerMap[commandStr];
                const handlerMethod = context[methodKey];
                routedHandlers[commandStr] = handlerMethod;
            });

            return routedHandlers;
        },

        mergeDom (defaultDom, dom={}) {
            let mergedDom = {};
            Object.keys(defaultDom).forEach((domKey) => {
                mergedDom[domKey] = defaultDom[domKey];
            });

            Object.keys(dom).forEach((domKey) => {
                mergedDom[domKey] = dom[domKey];
                mergedDom[domKey].selector = dom[domKey];
            });

            return mergedDom;
        },

        getDom (dom) {
            const rootEl = dom.el || document.body;
            Object.keys(dom).forEach((domKey) => {
                let selector, domEl;

                selector = dom[domKey];
                if (selector === null) {
                    return;
                } else if (typeof selector === 'object') {
                    selector = selector.selector || selector;
                }

                domEl = DOM.get(selector, rootEl);
                domEl.selector = selector;

                dom[domKey] = domEl;
            });
        },

        bindDomEvents (handlers, context) {
            const { dom } = context;

            Object.keys(handlers).forEach((eventElKey) => {
                const [eventKey, elemKey] = eventElKey.split(' @');
                const elem = elemKey ? dom[elemKey][0] : dom.el[0];
                const eventHandler = handlers[eventElKey];

                elem.addEventListener(eventKey, eventHandler);
            });
        },

        mergeProps (defaultProps, props={}) {
            let mergedProps = {};

            Object.keys(defaultProps).forEach((propKey) => {
                const propValue = props[propKey] || defaultProps[propKey];
                mergedProps[propKey] = propValue;
            });

            return mergedProps;
        },

        validateProps (props, requiredProps) {
            Object.keys(props).forEach((propKey) => {
                if (requiredProps.indexOf(propKey) > -1 && !props[propKey]) {
                    throw new Error(`${moduleName} requires prop: ${propKey}`);
                }
            });
        }
    };

    const moduleProto = {
        moduleConstructor: function (opts) {
            moduleProto.prepModule(opts);
            moduleProto.buildModule(opts);
            moduleProto.setupModule(opts);
            moduleProto.renderModule(opts);
            moduleProto.initModule(opts);
        },

        prepModule (opts) {
            const context = moduleUtils.createContext();

            if (moduleProps) {
                const mergedProps = moduleUtils.mergeProps(moduleProps, opts.props);
                context.extendWith({ props: mergedProps});

                if (moduleRequiredProps) {
                    moduleUtils.validateProps(mergedProps, moduleRequiredProps);
                }
            }

            opts.context = context;
        },

        buildModule (opts) {
            const { context } = opts;
            const boundMethods = moduleUtils.bindMethods(moduleMethods, context);

            if (boundMethods.render) {
                boundMethods.render = moduleUtils.wrapRenderMethod(boundMethods.render, opts);
            }

            context.extendWith(boundMethods);
            context.extendWith({mediator: opts.mediator});
        },

        setupModule (opts) {
            const { context } = opts;
            if (context.setup) {
                context.setup();
            }

            if (moduleHandlers) {
                moduleUtils.registerHandlers(opts.mediator, moduleHandlers, context);
            }
        },

        renderModule (opts) {
            let { context } = opts;
            let mergedDom;

            if (context.render) {
                return;
            }

            if (moduleDom) {
                mergedDom = moduleUtils.mergeDom(moduleDom, opts.dom);
                moduleUtils.getDom(mergedDom);
                context.extendWith({dom: mergedDom});
            }

            if (moduleHandlers.domEvents) {
                moduleUtils.registerDomHandlers(moduleHandlers.domEvents, context);
            }
        },

        initModule (opts) {
            const { context } = opts;

            if (context.init) {
                context.init();
            }
        },

        destroyModule () {

        }
    };

    return moduleProto.moduleConstructor;
};

export default Module;

// jshint strict: false
/* eslint-disable no-alert, no-console */

import './polyfills';
import AppContainer from './containers/AppContainer';

/**
 * Tyester() - Public interface to instatiate a Typester instance bound to a
 * dom element
 *
 * @access public
 * @param  {object} opts={} instance options
 * @param  {element} opts.dom.el The dom element to be the canvas for Typester
 * @return {appContainer} AppContainer instance
 *
 * @example
 * new Typester({
 *   dom: {
 *     el: domElement
 *   }
 * });
 */
const Typester = function (opts={}) {
    return new AppContainer({ dom: {el: opts.el }});
};

export default Typester;

// jshint strict: false

import Module from '../core/Module';
import commands from '../utils/commands';
import DOM from '../utils/DOM';
import zeroWidthSpace from '../utils/zeroWidthSpace';

import toolbarConfig from '../config/toolbar';

let validTags = toolbarConfig.getValidTags();
let blockTags = toolbarConfig.getBlockTags();
let listTags  = toolbarConfig.getListTags();

/**
* @access protected
*/
const BaseFormatter = Module({
    name: 'BaseFormatter',
    props: {},
    handlers: {
        requests: {},
        commands: {
            'format:export:to:canvas': 'exportToCanvas',
            'format:import:from:canvas': 'importFromCanvas',
            'format:default': 'formatDefault',
            'format:clean': 'formatClean'
        },
        events: {
            'contenteditable:newline': 'handleNewLine'
        }
    },
    methods: {
        init () {},

        exportToCanvas () {
            const { mediator } = this;
            const rootElement = mediator.get('selection:rootelement');
            const canvasBody = mediator.get('canvas:body');
            this.injectHooks(rootElement);

            const rangeCoordinates = mediator.get('selection:range:coordinates');
            const clonedNodes = this.cloneNodes(rootElement);
            mediator.exec('canvas:content', clonedNodes);
            mediator.exec('canvas:select:by:coordinates', rangeCoordinates);

            this.removeZeroWidthSpaces(canvasBody);
        },

        cloneNodes (rootElement) {
            let clonedNodes = [];
            rootElement.childNodes.forEach((node) => {
                clonedNodes.push(node.cloneNode(true));
            });
            return clonedNodes;
        },

        injectHooks (rootElement) {
            while (!/\w+/.test(rootElement.firstChild.textContent)) {
                DOM.removeNode(rootElement.firstChild);
            }

            while(!/\w+/.test(rootElement.lastChild.textContent)) {
                DOM.removeNode(rootElement.lastChild);
            }

            DOM.insertBefore(zeroWidthSpace.get(), rootElement.firstChild);
            DOM.insertAfter(zeroWidthSpace.get(), rootElement.lastChild);
        },

        importFromCanvas (opts={}) {
            const { mediator } = this;
            const canvasBody = mediator.get('canvas:body');

            mediator.exec('canvas:cache:selection');
            mediator.exec('format:clean', canvasBody);
            if (opts.importFilter) {
                opts.importFilter(canvasBody);
            }
            mediator.exec('canvas:select:cachedSelection');

            const canvasSelectionCoordinates = mediator.get('canvas:selection:coordinates');
            mediator.exec('selection:select:all');
            mediator.exec('contenteditable:inserthtml', canvasBody.innerHTML);
            mediator.exec('selection:select:coordinates', canvasSelectionCoordinates);

            mediator.emit('import:from:canvas:complete');
        },

        formatDefault () {
            const { mediator } = this;
            const rootElem = mediator.get('selection:rootelement');
            commands.defaultBlockFormat();
            this.removeStyledSpans(rootElem);
        },

        formatEmptyNewLine () {
            const { mediator } = this;
            const anchorNode = mediator.get('selection:anchornode');
            const canDefaultNewline = !(anchorNode.innerText && anchorNode.innerText.trim().length) && !DOM.isIn(anchorNode, toolbarConfig.preventNewlineDefault);
            const anchorIsContentEditable = anchorNode.hasAttribute && anchorNode.hasAttribute('contenteditable');

            if (canDefaultNewline || anchorIsContentEditable) {
                this.formatDefault();
            }
        },

        formateBlockquoteNewLine () {
            const { mediator } = this;

            commands.exec('outdent');
            this.formatDefault();

            const currentRangeClone = mediator.get('selection:range').cloneRange();
            const { startContainer } = currentRangeClone;

            if (startContainer.previousSibling && startContainer.previousSibling.nodeName === 'BLOCKQUOTE') {
                const brEls = startContainer.previousSibling.querySelectorAll('br');
                const divEls = startContainer.previousSibling.querySelectorAll('div');

                for (let i = 0; i < brEls.length; i++) {
                    DOM.removeNode(brEls[i]);
                }

                for (let i = 0; i < divEls.length; i++) {
                    DOM.unwrap(divEls[i]);
                }

                mediator.exec('selection:update:range', currentRangeClone);
            }

        },

        handleNewLine () {
            const { mediator } = this;
            const { startContainer } = mediator.get('selection:range');
            const containerIsEmpty = !/\w+/.test(startContainer.textContent);
            const containerIsBlockquote = DOM.isIn(startContainer, 'BLOCKQUOTE');
            const isContentEditable = startContainer.nodeType === Node.ELEMENT_NODE && startContainer.hasAttribute('contenteditable');

            if (containerIsBlockquote) {
                this.formateBlockquoteNewLine();
            } else if (containerIsEmpty || isContentEditable) {
                this.formatEmptyNewLine();
            }
        },

        formatClean (rootElem) {
            this.unwrapInvalidElements(rootElem);
            this.defaultOrphanedTextNodes(rootElem);
            this.removeBrNodes(rootElem);
            this.ensureRootElems(rootElem);
            this.removeStyleAttributes(rootElem);
            this.removeEmptyNodes(rootElem, { recursive: true });

            // -----

            // this.removeBrNodes(rootElem);
            // // this.removeEmptyNodes(rootElem);
            // this.removeFontTags(rootElem);
            // this.removeStyledSpans(rootElem);
            // this.clearEntities(rootElem);
            // this.removeZeroWidthSpaces(rootElem);
            // this.defaultOrphanedTextNodes(rootElem);
            // this.removeEmptyNodes(rootElem, { recursive: true });
        },

        removeStyleAttributes (rootElem) {
            const styleAttributeNodes = rootElem.querySelectorAll('[style]');
            styleAttributeNodes.forEach((styleAttributeNode) => {
                styleAttributeNode.removeAttribute('style');
            });
        },

        removeBrNodes (rootElem) {
            const brNodes = rootElem.querySelectorAll('br');
            let brNodesToProcess = [];
            let brNodesToRemove = [];

            brNodes.forEach((brNode) => {
                const skipNode = brNode.skipNode;

                if (skipNode) {
                    return;
                }

                const isLastChild = brNode === brNode.parentNode.lastChild;
                const isDoubleBreak = brNode.nextSibling && brNode.nextSibling.nodeName === 'BR';
                const isInBlock = DOM.isIn(brNode, blockTags, rootElem);

                if (isLastChild) {
                    brNodesToRemove.push(isLastChild);
                    return;
                }

                if (isDoubleBreak && isInBlock) {
                    brNodesToProcess.push([
                        brNode,
                        brNode.nextSibling
                    ]);
                    brNode.nextSibling.skipNode = true;
                    return;
                }
            });

            brNodesToProcess.forEach((brNodePair) => {
                const [firstBrNode, secondBrNode] = brNodePair;
                const closestBlock = DOM.getClosestInArray(firstBrNode, blockTags, rootElem);
                const newParagraph = document.createElement('p');
                let previousSibling;

                while ((previousSibling = firstBrNode.previousSibling)) {
                    if (newParagraph.firstChild) {
                        DOM.insertBefore(previousSibling, newParagraph.firstChild);
                    } else {
                        newParagraph.appendChild(previousSibling);
                    }

                    DOM.insertBefore(newParagraph, closestBlock);
                }

                DOM.removeNode(firstBrNode);
                DOM.removeNode(secondBrNode);
            });

            brNodesToRemove.forEach((brNode) => {
                DOM.removeNode(brNode);
            });
        },

        unwrapInvalidElements (rootElem) {
            const rootDoc = rootElem.ownerDocument;
            const walker = rootDoc.createTreeWalker(
                rootElem,
                NodeFilter.SHOW_ELEMENT,
                null,
                false
            );

            let invalidElements = [];
            while (walker.nextNode()) {
                let { currentNode } = walker;
                let isInvalid = validTags.indexOf(currentNode.nodeName) < 0;
                let isBrNode = currentNode.nodeName === 'BR'; // BR nodes are handled elsewhere
                let isTypesterElem = currentNode.className && /typester/.test(currentNode.className);

                if (isInvalid && !isBrNode && !isTypesterElem) {
                    invalidElements.unshift(currentNode);
                }
            }

            invalidElements.forEach((invalidElement) => {
                let unwrappedNodes = DOM.unwrap(invalidElement, { keepNode: true });
                if (!DOM.isIn(invalidElement, validTags, rootElem) && unwrappedNodes.length) {
                    let newParagraph = rootDoc.createElement('p');
                    unwrappedNodes.forEach((unwrappedNode) => {
                        newParagraph.appendChild(unwrappedNode);
                    });
                    DOM.insertBefore(newParagraph, invalidElement);
                }
                DOM.removeNode(invalidElement);
            });
        },

        defaultOrphanedTextNodes (rootElem) {
            const { childNodes } = rootElem;
            for (let i = 0; i < childNodes.length; i++) {
                let childNode = childNodes[i];
                if (childNode.nodeType === Node.TEXT_NODE && /\w+/.test(childNode.textContent)) {
                    let newParagraph = document.createElement('p');
                    DOM.insertBefore(newParagraph, childNode);
                    newParagraph.appendChild(childNode);
                    while (newParagraph.nextSibling && blockTags.concat(listTags).indexOf(newParagraph.nextSibling.nodeName) < 0) {
                        newParagraph.appendChild(newParagraph.nextSibling);
                    }
                }
            }
        },

        clearEntities (rootElem) {
            const rootDoc = rootElem.ownerDocument;
            const walker = rootDoc.createTreeWalker(
                rootElem,
                NodeFilter.SHOW_TEXT,
                null,
                false
            );

            let textNodes = [];
            while(walker.nextNode()) {
                textNodes.push(walker.currentNode);
            }

            textNodes.forEach((textNode) => {
                if (/\w+/.test(textNode.textContent)) {
                    textNode.nodeValue = textNode.nodeValue.replace(/^\u00a0/, '');
                    textNode.nodeValue = textNode.nodeValue.replace(/\u00a0$/, '');
                }
            });
        },

        ensureRootElems (rootElem) {
            const rootNodeTags = ['H1', 'H2', 'H3', 'H4', 'H5', 'H6', 'OL', 'UL', 'BLOCKQUOTE', 'P'];
            const nestableTags = [
                {
                    tags: ['OL', 'UL'],
                    validParents: ['OL', 'UL', 'LI']
                },
                {
                    tags: ['H1', 'H2', 'H3', 'H4', 'H5', 'H6'],
                    validParents: ['LI']
                }
            ];
            const rootNodes = rootElem.querySelectorAll(rootNodeTags.join(', '));
            const validNesting = function (node) {
                let validNesting = false;
                nestableTags.forEach((nestingDict) => {
                    if (
                        nestingDict.tags.indexOf(node.tagName) > -1 &&
                        nestingDict.validParents.indexOf(node.parentNode.tagName) > -1
                    ) {
                        validNesting = true;
                    }
                });
                return validNesting;
            };

            const moveNodeToRoot = function (node) {
                if (
                    node.parentNode === rootElem
                ) {
                    return;
                }

                if (validNesting(node)) {
                    return;
                }

                let rootParentNode = node.parentNode;
                if (node.tagName === 'P' && ['LI', 'BLOCKQUOTE'].indexOf(rootParentNode.tagName) > -1) {
                    while (node.firstChild) {
                        DOM.insertBefore(node.firstChild, node);
                    }
                    DOM.removeNode(node);
                    return;
                }

                while (
                    rootParentNode.parentNode !== rootElem
                ) {
                    rootParentNode = rootParentNode.parentNode;
                }

                DOM.insertBefore(node, rootParentNode);
            };

            rootNodes.forEach(moveNodeToRoot);
        },

        removeZeroWidthSpaces (rootElem) {
            rootElem.childNodes.forEach((childNode) => {
                if (
                    childNode.nodeType === Node.TEXT_NODE &&
                    zeroWidthSpace.assert(childNode)
                ) {
                    DOM.removeNode(childNode);
                }
            });
        },

        removeEmptyNodes (rootElem, opts={}) {
            if (rootElem.normalize) {
                rootElem.normalize();
            }

            for (let i = rootElem.childNodes.length - 1; i >= 0; i--) {
                let childNode = rootElem.childNodes[i];

                if (childNode.nodeName === 'BR') {
                    continue;
                }

                if (opts.recursive && childNode.childNodes.length) {
                    this.removeEmptyNodes(childNode, { recursive: true, rootIsChild: true });
                }

                if (!/[\w\.,\/#!$%\^&\*;:{}=\-_`~()\'\"]/.test(childNode.textContent)) {
                    if (
                        (!opts.rootIsChild && !zeroWidthSpace.assert(childNode) && i > 0) ||
                        childNode.nodeType === Node.ELEMENT_NODE
                    ) {
                        DOM.removeNode(childNode);
                    }
                }
            }
        },

        removeStyledSpans (rootElem) {
            const styledSpans = rootElem.querySelectorAll('span[style]');
            for (let i = styledSpans.length - 1; i >= 0; i--) {
                let styledSpan = styledSpans[i];
                while (styledSpan.firstChild) {
                    DOM.insertBefore(styledSpan.firstChild, styledSpan);
                }
                DOM.removeNode(styledSpan);
            }
        },

        removeFontNodes (rootElem) {
            const fontTags = rootElem.querySelectorAll('font');
            for (let i = fontTags.length - 1; i >= 0; i--) {
                let fontTag = fontTags[i];
                while (fontTag.firstChild) {
                    DOM.insertBefore(fontTag.firstChild, fontTag);
                }
                DOM.removeNode(fontTag);
            }
        }
    }
});

export default BaseFormatter;

// jshint strict: false

import Module from '../core/Module';
import commands from '../utils/commands';
import DOM from '../utils/DOM';

/**
* @access protected
*/
const BlockFormatter = Module({
    name: 'BlockFormatter',
    props: {
        selectionRootEl: null
    },
    handlers: {
        commands: {
            'format:block': 'formatBlock'
        },
        events: {}
    },
    methods: {
        init () {},

        formatBlock (opts) {
            this.preProcess(opts);
            this.process(opts);
            this.commit(opts);
        },

        preProcess () {
            const { mediator } = this;
            mediator.exec('format:export:to:canvas');
        },

        process (opts) {
            const { mediator } = this;
            const canvasDoc = mediator.get('canvas:document');

            if (opts.toggle) {
                if (opts.style === 'BLOCKQUOTE') {
                    commands.exec('outdent', null, canvasDoc);
                }
                commands.defaultBlockFormat(canvasDoc);
            } else {
                commands.formatBlock(opts.style, canvasDoc);
            }
        },

        commit (opts) {
            const { mediator, cleanupBlockquote } = this;
            const importFilter = opts.style === 'BLOCKQUOTE' ? cleanupBlockquote : null;
            mediator.exec('format:import:from:canvas', { importFilter });
        },

        cleanupBlockquote (rootElem) {
            const blockquoteParagraphs = rootElem.querySelectorAll('BLOCKQUOTE P');
            blockquoteParagraphs.forEach((paragraph) => {
                DOM.unwrap(paragraph);
            });
        }
    }
});

export default BlockFormatter;

// jshint strict: false

import Module from '../core/Module';
import DOM from '../utils/DOM';
import zeroWidthSpace from '../utils/zeroWidthSpace';

import canvasStyles from '../../styles/canvas.scss';

/**
* @access protected
*/
const Canvas = Module({
    name: 'Canvas',
    props: {
        iframe: null,
        relativeRange: null,
        alreadyContainered: false,
        cachedSelection: null
    },
    handlers: {
        requests: {
            'canvas:document' : 'getCanvasDocument',
            'canvas:window' : 'getCanvasWindow',
            'canvas:body' : 'getCanvasBody',
            'canvas:formatted:block': 'getFormattedBlock',
            'canvas:selection:coordinates' : 'getSelectionCoordinates',
            'canvas:selection': 'getSelection',
            'canvas:selection:in:or:contains': 'selectionInOrContains'
        },
        commands: {
            'canvas:content' : 'setContent',
            'canvas:insert:range' : 'insertRange',
            'canvas:insert:node' : 'insertNode',
            'canvas:select:all' : 'selectAll',
            'canvas:select:by:coordinates' : 'selectByCoordinates',
            'canvas:import:selection' : 'importSelection',
            'canvas:export:prep': 'exportPrep',
            'canvas:export:all': 'exportAll',
            'canvas:cache:selection': 'cacheSelection',
            'canvas:select:cachedSelection': 'selectCachedSelection',
            'canvas:select:ensure:offsets': 'ensureSelectionOffsets'
        },
        events: {
            'app:destroy' : 'destroy'
        }
    },
    methods: {
        init () {
            this.appendStyles();
            this.createIframe();
        },

        appendStyles () {
            const { props } = this;
            props.styles = DOM.addStyles(canvasStyles);
        },

        createIframe () {
            const { mediator } = this;
            const iframe = document.createElement('iframe');

            iframe.classList.add('typester-canvas');
            iframe.onload = () => {
                this.setCanvasBodyEditable();
                this.applyPolyfills();
                mediator.emit('canvas:created');
            };

            this.props.iframe = iframe;
            document.body.appendChild(iframe);
        },

        clearIframe () {
            const canvasBody = this.getCanvasBody();
            canvasBody.innerHTML = '';
        },

        reset () {
            const { props } = this;
            props.relativeRange = null;
            props.alreadyContainered = false;
            props.cachedSelection = null;
            this.clearIframe();
        },

        cacheSelection () {
            const { props, mediator } = this;

            mediator.exec('selection:ensure:text:only');

            const {
                anchorNode,
                anchorOffset,
                focusNode,
                focusOffset
            } = mediator.get('selection:current');

            props.cachedSelection = {
                anchorNode,
                anchorOffset,
                focusNode,
                focusOffset
            };
        },

        selectCachedSelection () {
            const { props, mediator } = this;

            const {
                anchorNode,
                anchorOffset,
                focusNode,
                focusOffset
            } = props.cachedSelection;

            const canvasDoc = this.getCanvasDocument();
            const newRange = canvasDoc.createRange();

            newRange.setStart(anchorNode, anchorOffset);
            newRange.setEnd(focusNode, focusOffset);

            mediator.exec('selection:update:range', newRange);
        },

        ensureSelectionOffsets () {
            const { props, mediator } = this;

            if (!props.cachedSelection) {
                return;
            }

            const {
                anchorNode: currentAnchorNode,
                anchorOffset: currentAnchorOffset,
                focusNode: currentFocusNode,
                focusOffset: currentFocusOffset
            } = mediator.get('selection:current');

            const {
                anchorOffset: cachedAnchorOffset,
                focusOffset: cachedFocusOffset
            } = props.cachedSelection;

            const anchorOffsetMismatch = currentAnchorOffset !== cachedAnchorOffset;
            const focusOffsetMismatch = currentFocusOffset !== cachedFocusOffset;

            if (anchorOffsetMismatch || focusOffsetMismatch) {
                const canvasDoc = this.getCanvasDocument();
                const newRange = canvasDoc.createRange();

                newRange.setStart(currentAnchorNode, cachedAnchorOffset);
                newRange.setEnd(currentFocusNode, cachedFocusOffset);

                mediator.exec('selection:update:range', newRange);
            }
        },

        setCanvasBodyEditable () {
            const { props } = this;
            const canvasBody = props.iframe.contentDocument.body;
            canvasBody.contentEditable = true;
        },

        applyPolyfills () {
            const canvasWindow = this.getCanvasWindow();
            if (canvasWindow.NodeList && !canvasWindow.NodeList.prototype.forEach) {
                canvasWindow.NodeList.prototype.forEach = function (callback, thisArg) {
                    thisArg = thisArg || canvasWindow;
                    for (var i = 0; i < this.length; i++) {
                        callback.call(thisArg, this[i], i, this);
                    }
                };
            }
        },

        // Handlers
        getCanvasDocument () {
            const { props } = this;
            return props.iframe.contentDocument;
        },

        getCanvasWindow () {
            const { props } = this;
            return props.iframe.contentWindow;
        },

        getCanvasBody () {
            const { props } = this;
            return props.iframe.contentDocument.body;
        },

        setContent (html) {
            const canvasDoc = this.getCanvasDocument();

            if (html instanceof Array) {
                this.reset();
                html.forEach((node) => {
                    canvasDoc.body.appendChild(node);
                });
            } else {
                canvasDoc.body.innerHTML = html;
            }
        },

        insertRange (range) {
            const rangeDocFrag = range.cloneContents();
            const canvasBody = this.getCanvasBody();

            this.reset();

            for (let i = 0; i < rangeDocFrag.childNodes.length; i++) {
                let childNode = rangeDocFrag.childNodes[i];
                if (
                    childNode.nodeType === Node.TEXT_NODE &&
                    (
                        !/\w+/.test(childNode.textContent) ||
                        zeroWidthSpace.assert(childNode)
                    )
                ) {
                    rangeDocFrag.removeChild(childNode);
                }
            }

            canvasBody.appendChild(rangeDocFrag);
        },

        insertNode (node) {
            const nodeClone = node.cloneNode(true);
            const canvasBody = this.getCanvasBody();
            this.reset();
            canvasBody.appendChild(nodeClone);
        },

        selectAll (opts={}) {
            const { mediator } = this;
            mediator.exec('selection:select:all', opts);
        },

        selectByCoordinates (rangeCoordinates) {
            const { mediator } = this;
            const canvasBody = this.getCanvasBody();

            mediator.exec('selection:set:el', canvasBody);
            mediator.exec('selection:select:coordinates', rangeCoordinates);
        },

        importSelection (opts={}) {
            const { mediator } = this;
            let rangeCoordinates;

            if (opts.toRoot) {
                rangeCoordinates = mediator.get('selection:range:relative:toroot');
                mediator.exec('selection:expand:toroot');
            }

            const selectionRange = mediator.get('selection:range');

            this.insertRange(selectionRange);
            if (opts.toRoot) {
                this.selectByCoordinates(rangeCoordinates);
            } else {
                this.selectAll();
            }
            this.setCanvasBodyEditable();

        },

        exportPrep () {
            this.cleanHtml();
        },

        exportAll () {
            const { mediator } = this;
            const canvasBody = this.getCanvasBody();
            // this.exportPrep();

            let innerHTML = canvasBody.innerHTML;
            innerHTML = innerHTML.replace(/\s{2,}/g, ' ');
            innerHTML = innerHTML.replace(/\r?\n|\r/g, '');

            mediator.exec('contenteditable:inserthtml', innerHTML);
        },

        getFormattedBlock () {
            const { mediator } = this;
            mediator.exec('selection:expand:toroot');
            const blockRange = mediator.get('selection:range');
            return blockRange.cloneContents();
        },

        cleanHtml () {
            const canvasDoc = this.getCanvasDocument();
            const canvasBody = this.getCanvasBody();
            const walker = canvasDoc.createTreeWalker(canvasBody, NodeFilter.SHOW_ALL, null, false);

            let allNodes = [];

            while (walker.nextNode()) {
                allNodes.push(walker.currentNode);
            }

            for (let i = allNodes.length - 1; i >= 0; i--) {
                let node = allNodes[i];

                if (
                    !node.textContent ||
                    !node.textContent.trim().length ||
                    (zeroWidthSpace.assert(node))
                ) {
                    DOM.removeNode(node);
                } else if (
                    node.classList && node.classList.contains('typester-replace-default')
                ) {
                    let defaultNode = document.createElement('p');
                    DOM.insertAfter(defaultNode, node);
                    defaultNode.appendChild(node);
                    DOM.unwrap(node);
                } if (
                    (node.classList && node.classList.contains('typester-container')) ||
                    (node.nodeName === 'SPAN' && node.hasAttribute('style')) ||
                    node.nodeName === 'FONT' || node.nodeName === 'DIV'
                ) {
                    DOM.unwrap(node);
                }
            }
        },

        getSelection () {
            const { mediator } = this;
            return mediator.get('selection:current');
        },

        getSelectionCoordinates () {
            const { mediator } = this;
            return mediator.get('selection:range:coordinates');
        },

        selectionInOrContains (selectors) {
            const { mediator } = this;
            return mediator.get('selection:in:or:contains', selectors);
        },

        destroy () {
            const { props } = this;
            const { iframe } = props;
            iframe.parentNode.removeChild(iframe);
        }
    }
});

export default Canvas;

// jshint strict: false

import Module from '../core/Module';
import DOM from '../utils/DOM';
import keycodes from '../utils/keycodes';

import contentEditableStyles from '../../styles/contentEditable.scss';

/**
* @access protected
*/
const ContentEditable = Module({
    name: 'ContentEditable',
    props: {
        styles: null,
        cleanupTimeout: null
    },
    dom: {},
    handlers: {
        requests: {
            'contenteditable:element': 'getContentEditableElement',
            'contenteditable:toolbar:buttons': 'getToolbarButtons'
        },
        commands: {
            'contenteditable:inserthtml' : 'insertHTML',
            'contenteditable:refocus' : 'reFocus',
            'contenteditable:cleanup' : 'cleanup'
        },
        domEvents: {
            'focus' : 'handleFocus',
            'keydown' : 'handleKeydown',
            'keyup' : 'handleKeyup',
            'blur' : 'handleBlur',
            'paste' : 'handlePaste',
            'mouseover' : 'handleMouseOver',
            'mouseout' : 'handleMouseOut',
            'click' : 'handleClick',
            'input' : 'handleInput'
        }
    },
    methods: {
        setup () {
            this.appendStyles();
        },

        init () {
            this.ensureClass();
            this.ensureEditable();
            this.updatePlaceholderState();
            this.updateValue();
        },

        appendStyles () {
            const { props } = this;
            props.styles = DOM.addStyles(contentEditableStyles);
        },

        ensureClass () {
            const { dom } = this;
            dom.el[0].classList.add('typester-content-editable');
        },

        updatePlaceholderState () {
            const { dom } = this;
            const rootEl = dom.el[0];

            if (rootEl.hasAttribute('data-placeholder')) {
                if (
                    (rootEl.childNodes.length && rootEl.textContent.trim().length)
                ) {
                    rootEl.classList.remove('show-placeholder');
                } else {
                    rootEl.classList.add('show-placeholder');
                }
            }
        },

        updateValue () {
            const { dom } = this;
            const rootEl = dom.el[0];

            if (rootEl.innerText.trim().length) {
                rootEl.value = rootEl.innerHTML;
            } else {
                rootEl.value = '';
            }
        },

        ensureEditable () {
            const { dom } = this;
            const rootEl = dom.el[0];

            if (!rootEl.hasAttribute('contenteditable')) {
                rootEl.contentEditable = true;
            }
        },

        ensureDefaultBlock () {
            const { dom, mediator } = this;
            const rootEl = dom.el[0];

            if (!/\w+/.test(rootEl.textContent)) {
                rootEl.innerHTML = '<p>&nbsp;</p>';
                mediator.exec('selection:select:contents', rootEl.childNodes[0]);
            }
        },

        getToolbarButtons () {
            const { dom } = this;
            const { toolbarButtons } = dom.el[0].dataset;
            let buttonArray = [];

            if (toolbarButtons) {
                buttonArray = JSON.parse(toolbarButtons);
            }

            return buttonArray;
        },

        insertHTML (html) {
            const { mediator } = this;

            if (document.queryCommandSupported('insertHTML')) {
                document.execCommand('insertHTML', null, html);
            } else {
                let currentSelection = mediator.get('selection:current');
                let currentRange = mediator.get('selection:range');

                currentRange.deleteContents();

                let tmpContainer = document.createElement('container');
                tmpContainer.innerHTML = html;

                let docFrag = document.createDocumentFragment();
                let node, lastNode;

                while ((node = tmpContainer.firstChild)) {
                    lastNode = docFrag.appendChild(node);
                }
                currentRange.insertNode(docFrag);

                if (lastNode) {
                    currentRange = currentRange.cloneRange();
                    currentRange.setStartAfter(lastNode);
                    currentRange.collapse(true);
                    currentSelection.removeAllRanges();
                    currentSelection.addRange(currentRange);
                }
            }
        },

        reFocus () {
            const { dom } = this;
            dom.el[0].focus();
        },

        getContentEditableElement () {
            const { dom } = this;
            return dom.el[0];
        },

        cleanup () {
            const { dom, mediator } = this;
            const rootEl = dom.el[0];
            mediator.exec('format:clean', rootEl);
        },

        setCleanupTimeout () {
            const { props } = this;
            props.cleanupTimeout = setTimeout(() => {
                this.cleanup();
            }, 250);
        },

        clearCleanupTimeout () {
            const { props } = this;
            if (props.cleanupTimeout) {
                clearTimeout(props.cleanupTimeout);
                props.cleanupTimeout = null;
            }
        },

        // DOM Event Handlers
        handleFocus () {
            const { mediator } = this;
            this.clearCleanupTimeout();
            this.ensureDefaultBlock();
            this.updatePlaceholderState();
            mediator.emit('contenteditable:focus');
        },

        handleKeyup (evnt) {
            const { mediator, dom } = this;
            const keyCode = evnt.which || evnt.keyCode;
            const anchorNode = mediator.get('selection:anchornode');

            function CustomEvent(event, params) {
                var evt;
                params = params || { bubbles: true, cancelable: true, detail: undefined };
                evt = document.createEvent('CustomEvent');
                evt.initCustomEvent(event, params.bubbles, params.cancelable, params.detail);
                return evt;
            }

            switch (keyCode) {
            case keycodes.ENTER:
                setTimeout(() => {
                    mediator.emit('contenteditable:newline');
                }, 100);
                break;
            case keycodes.BACKSPACE:
                if (!anchorNode.textContent.trim().length || (anchorNode.hasAttribute && anchorNode.hasAttribute('contenteditable'))) {
                    mediator.emit('contenteditable:newline');
                }
                break;
            case keycodes.TAB:
                mediator.emit('contenteditable:tab:up', evnt);
                break;
            }

            dom.el[0].dispatchEvent(new CustomEvent('change'));

            this.updateValue();
            this.updatePlaceholderState();
        },

        handleKeydown (evnt) {
            const { mediator } = this;
            const keyCode = evnt.which || evnt.keyCode;

            setTimeout(() => {
                this.updatePlaceholderState();
            }, 25);

            switch (keyCode) {
            case keycodes.TAB:
                mediator.emit('contenteditable:tab:down', evnt);
                break;
            }
        },

        handleBlur () {
            const { mediator } = this;
            this.updatePlaceholderState();
            this.updateValue();
            this.setCleanupTimeout();
            mediator.emit('contenteditable:blur');
        },

        handlePaste (evnt) {
            const { mediator } = this;
            mediator.emit('contenteditable:paste', evnt);
            this.updateValue();
        },

        handleMouseOver (evnt) {
            const { mediator } = this;
            if (evnt.target.nodeName === 'A') {
                mediator.emit('contenteditable:mouseover:anchor', evnt);
            }
        },

        handleMouseOut (evnt) {
            const { mediator } = this;
            if (evnt.target.nodeName === 'A') {
                mediator.emit('contenteditable:mouseout:anchor', evnt);
            }
        },

        handleClick (evnt) {
            const { dom } = this;
            const rootEl = dom.el[0];

            if (DOM.isIn(evnt.target, 'A', rootEl)) {
                evnt.preventDefault();
                evnt.stopPropagation();
            }
        },

        handleInput () {
            this.updateValue();
        }
    }
});

export default ContentEditable;

// jshint strict: false
import Module from '../core/Module';
import DOM from '../utils/DOM';

import flyoutTemplate from '../../templates/flyout.html';
import flyoutStyles from '../../styles/flyout.scss';

/**
* @access protected
*/
const Flyout = Module({
    name: 'Flyout',
    dom: {},
    props: {
        minZIndex: 90,
        styles: null,
        flyouts: []
    },
    handlers: {
        requests: {
            'flyout:new' : 'newFlyout'
        },
        commands: {},
        events: {
            'app:destroy' : 'destroy'
        }
    },
    methods: {
        setup () {
            this.appendStyles();
        },

        init () {},

        appendStyles () {
            const { props } = this;
            props.styles = DOM.addStyles(flyoutStyles);
        },

        newFlyout () {
            const { props } = this;
            const flyout = this.buildFlyout();
            props.flyouts.push(flyout);
            return flyout;
        },

        buildFlyout () {
            const flyout = {
                el: this.buildTemplate(),
                appended: null
            };

            flyout.contentEl = flyout.el.querySelector('.typester-flyout-content');

            flyout.clearContent = () => {
                flyout.contentEl.innerHTML = '';
            };

            flyout.appendContent = (content) => {
                return DOM.appendTo(flyout.contentEl, content);
            };
            flyout.show = () => {
                this.showFlyout(flyout);
            };
            flyout.remove = () => {
                this.removeFlyout(flyout);
            };
            flyout.hide = () => {
                this.hideFlyout(flyout);
            };
            flyout.position = (coordinates) => {
                this.positionFlyout(flyout, coordinates);
            };
            flyout.setPlacement = (placement) => {
                this.setPlacement(flyout, placement);
            };

            return flyout;
        },

        buildTemplate () {
            const wrapperEl = document.createElement('div');
            let flyoutHTML, flyoutEl;

            flyoutHTML = flyoutTemplate();
            if (typeof flyoutHTML === 'string') {
                wrapperEl.innerHTML = flyoutHTML;
            } else {
                wrapperEl.appendChild(flyoutHTML[0]);
            }
            flyoutEl = wrapperEl.childNodes[0];

            return flyoutEl;
        },

        appendFlyout (flyout) {
            DOM.appendTo(document.body, flyout.el);
        },

        removeFlyout (flyout) {
            if (flyout.appended) {
                DOM.removeNode(flyout.el);
                flyout.appended = false;
            }
        },

        positionFlyout (flyout, coordinates) {
            const { mediator, props } = this;
            const contentEditableEl = mediator.get('contenteditable:element');
            const containerZIndex = Math.max(props.minZIndex, DOM.getContainerZIndex(contentEditableEl));
            console.log('positionFlyout', coordinates);
            Object.keys(coordinates).forEach((coordinateKey) => {
                flyout.el.style[coordinateKey] = coordinates[coordinateKey];
            });

            flyout.el.style.zIndex = containerZIndex + 1;
        },

        setPlacement (flyout, placement='above') {
            flyout.el.classList.remove('place-below');
            flyout.el.classList.remove('place-above');
            flyout.el.classList.add(`place-${placement}`);
        },

        showFlyout (flyout) {
            if (!flyout.appended) {
                this.appendFlyout(flyout);
                flyout.appended = true;
            } else {
                flyout.el.style.display = 'block';
            }
        },

        hideFlyout (flyout) {
            flyout.el.style.display = 'none';
        },

        destroy () {
            const { props } = this;
            props.flyouts.forEach((flyout) => {
                flyout.remove();
            });
        }

    }
});

export default Flyout;

